package main

import (
	"encoding/pem"
	"fmt"
	"log"

	"go.mozilla.org/pkcs7"
)

func main() {
	derBlock, _ := pem.Decode([]byte(`-----BEGIN PKCS7-----

-----END PKCS7-----`))
	sig, err := pkcs7.Parse(derBlock.Bytes)
	if err != nil {
		log.Fatal(err)
	}
	sig.Content = []byte("cariboumaurice\n")
	err = sig.Verify()
	if err != nil {
		log.Fatal(err)
	}
	fmt.Println("verified")
}
